package ex02if_else;

public class Ex01ifelse문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// if-else
		//참, 거짓 두가지 상황에서 제어를 하고 싶을 때 사용하는 조건문
		
		int num = 10;
		
		if(num%2 == 1)
		{
		System.out.println(num + " (은)는 홀수 입니다.");	
		}else {
			System.out.println(num + "(은)는 짝수 입니다.");
		}
	}

}
